mapper.select:
SELECT 
	
FROM poto a

WHERE a.id = #{id}

SELECT
FROM poto a
AND a.user_id = #{userId}
AND a.img_label = #{imgLabel}
AND a.content LIKE '%'||#{content}||'%' '%'+#{content}+'%' concat('%',#{content},'%')
AND a.position LIKE '%'||#{position}||'%' '%'+#{position}+'%' concat('%',#{position},'%')
AND a.syn = #{syn}
ORDER BY ${page.orderBy}

SELECT 
	
FROM poto a


	
		

	
		ORDER BY ${page.orderBy}
